import { useState } from "react";
export const TaskList =  (props) => {
  const [tasks, setTask] = useState([
    {id: 134, name: "Studying hour", competed: false},
    {id: 132, name: "sleeping hour", competed: false},
    {id: 138, name: "exercise hour", competed: true}
    ])
  const [show, setShow] = useState(true)
  function handleDelete(id){
    setTask(tasks.filter((task) => task.id != id))
  }
    return (
        <div className="Container">
        <h1> Task List {props.title} </h1>
        <ul className="Listed">
        <button onClick={()=> setShow(!show)} className="Toggle">Toggle</button>
          { show && tasks.map((task) => (
            <li className={task.competed ? "completed" : "notcompleted"} key={task.id}> 
              <span>{task.id} - {task.name}  <button onClick={() => handleDelete(task.id)} className="Delete">
                    Delete
                </button>
              </span>
            </li>
          ))}
         </ul>
    </div>
      )
}
