import { useState } from "react";
import {CardList} from "./CardList"
import {TaskBox} from "./TaskBox"
import "./TaskList.css"
export const TaskList =  ({tasks, setTask}) => {
  const [show, setShow] = useState(true)
  const style = {
    border: "2px solid",
    borderColor: show ? "green" : "red",

  }

  function handleDelete(id){
    setTask(tasks.filter((task) => task.id != id))
  }
    return (
        <div className="Container">
        <h1 style = {style}> Task List </h1>
        <ul className="Listed">
        <button onClick={()=> setShow(!show)} className="Toggle">{show ? "hide" : "show"}</button>
          { show && tasks.map((task) => (
            <CardList task={task} handleDelete={handleDelete} key={task.id}/>
          ))}
         </ul>
        <TaskBox result="warning">
        <p className="title">lorem ipsom 1</p>
        <p className="Description">loren ipsom changing the default fo the page</p>
      </TaskBox>
      <TaskBox result="success">
        <p className="title">lorem ipsom channel 2</p>
        <p className="Description">loren ipsom changing the default fo the page for success</p>
      </TaskBox>
    </div>
      )
}
