import "./AddTask.css"
import {useState} from "react"
export const AddTask = (tasks, setTask)=>{
  const [taskValue, setTaskValue] = useState("");
  const [progress, setProgress] = useState(false);


  const setChange = (event) => {
    setTaskValue(event.target.value)
  }
  const handleReset = (event) =>{
    setTaskValue("")
    setProgress(false)
  }

  const handleSubmit = (event)=>{
    event.preventDefault();
    const task = {
      id : Math.floor(Math.random() *10000),
      name: taskValue,
      completed: progress
    }
    setTask([...tasks, task]);
    handleReset();
  }
  return(
    <section className="AddTask">
      <form onSubmit={handleSubmit}>
        <input type="name" name="task" id="task" placeholder="Add Task" onChange={setChange} value={taskValue} />
        <select onChange={(e)=>setProgress(e.target.value)} value={progress}>
          <option value={false}>pending</option>
          <option value={true}>completed</option>
        </select>
        <button className="AddTaskButton" type="submit">Add Task</button>
        <span className="Reset" onClick={handleReset}>Reset</span>
      </form>
      <p>{taskValue}</p>
    </section>
    )
}
